-- problem8 ________________________________________________________________________________________
-- >>> problem8
-- 23514624000

number :: [Char]
number =
  show
    

problem8 :: Int
problem8 = greatestProduct number
 where
  greatestProduct x
    | length x < 13 = 0
    | otherwise = max (product . map digitToInt $ take 13 x)
                      (greatestProduct $ tail x)

